type WeatherType = 'clear' | 'rain' | 'snow' | 'wind' | 'clouds' | 'storm' | 'sunset' | 'night';

interface WeatherState {
  type: WeatherType;
  intensity: 'light' | 'medium' | 'heavy';
  duration?: number;
}

const WeatherAnimations: React.FC = () => {
  return (
    <CopilotKit
